//! –†–ê–ë–û–¢–ê —Å useState()
{
	// üî≥ ‚Å°‚Å¢‚Å£‚Å£–ò–ó–ú–ï–ù–ï–ù–ò–ï –ú–ê–°–°–ò–í–ê –û–ë–™–ï–ö–¢–û–í‚Å°
	{
		const [todos, setTodos] = useState([{ id: 1, text: "", completed: false }, { id: 2, text: "", completed: false }])
		// ‚ö™ ‚Å°‚Å£‚Å£‚Å¢–î–æ–±–∞–≤–ª–µ–Ω–∏–µ –Ω–æ–≤–æ–≥–æ –æ–±—ä–µ–∫—Ç–∞‚Å°
		{
			setTodos((prevTodos) => {
				return [
					...prevTodos,
					{
						id: nextId,
						text: inputValue,
						completed: false,
					},
				];
			});
		}
		// ‚ö™ ‚Å°‚Å£‚Å£‚Å°‚Å£‚Å£‚Å¢–ò–∑–º–µ–Ω–∏—Ç—å –æ—Ç–¥–µ–ª—å–Ω–æ–µ –ø–æ–ª–µ —É –∫–æ–Ω–∫—Ä–µ—Ç–Ω–æ–≥–æ id‚Å°
		{
			setTodos((prevTodos) =>
				prevTodos.map((todo) => (todo.id === 2 ? { ...todo, completed: !todo.completed } : todo)),
			);
		}
		// ‚ö™ ‚Å°‚Å£‚Å£‚Å¢–£–¥–∞–ª–∏—Ç—å –æ–±—ä–µ–∫—Ç –ø–æ –∫–æ–Ω–∫—Ä–µ—Ç–Ω–æ–º—É id‚Å°
		{
			// –≤–æ–∑–≤—Ä–∞—â–∞–µ—Ç –º–∞—Å—Å–∏–≤ –ø–æ —É—Å–ª–æ–≤–∏—é, –ø–µ—Ä–µ–±–∏—Ä–∞–µ—Ç –º–∞—Å—Å–∏–≤ –∏ –≤—ã–±–∏—Ä–∞–µ—Ç –∏–∑ –Ω–µ–≥–æ —ç–ª–µ–º–µ–Ω—Ç—ã, –∫–æ—Ç–æ—Ä—ã–µ –ø—Ä–æ—Ö–æ–¥—è—Ç –ø—Ä–æ–≤–µ—Ä–∫—É –ø–æ –∑–∞–¥–∞–Ω–Ω–æ–º—É —É—Å–ª–æ–≤–∏—é.
			setTodos((prevTodos) => prevTodos.filter((todo) => todo.id !== id));
		}
	}
	
}